import { useState } from "react";
import "./App.css";

function App() {
  const [inText, setInText] = useState("");
  const [itemList, setItemList] = useState([]);

  function addList() {
    setItemList([...itemList, inText]);
    setInText("");
  }

  return (
    <div className="App">
      <h1>ToDo List</h1>
      <div className="App-header">
        <input
          onChange={(e) => setInText(e.target.value)}
          value={inText}
          placeholder="digite aqui"
        />
        <button onClick={addList} style={{ marginLeft: 20 }}>
          Adicionar
        </button>
        <ul>
          {itemList?.length ? (
            itemList.map((item, index) => <li>{(index, item)}</li>)
          ) : (
            <p>Lista v√°zia</p>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
