import { useState } from "react";
import "./App.css";

function App() {
  const [inText, setInText] = useState("");
  const [itemList, setItemList] = useState([]);

  function onAddList() {
    if (!inText) {
      console.log("digite algo");
    } else {
      setItemList([...itemList, inText]);
      setInText("");
    }
  }

  function onRemove(item) {
    console.log(item);
    //setItemList(itemList.filter((nameItem) => nameItem !== item));
  }

  return (
    <div className="App">
      <h1>ToDo List</h1>
      <div className="App-header">
        <input
          onChange={(e) => setInText(e.target.value)}
          value={inText}
          placeholder="digite aqui"
        />
        <button onClick={onAddList} style={{ marginLeft: 20 }}>
          Adicionar
        </button>
        <ul>
          {itemList?.length ? (
            itemList.map((item) => (
              <span key={item.id}>
                <li className="li">
                  {item} <button onClick={() => onRemove(item)}>x</button>
                </li>
              </span>
            ))
          ) : (
            <p>Lista v√°zia</p>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
