import { useState } from "react";
import "./App.css";

function App() {
  const [inText, setInText] = useState("");
  const [itemList, setItemList] = useState([]);

  function addList() {
    if (!inText) {
      console.log("digite algo");
    } else {
      setItemList([...itemList, inText]);
      setInText("");
    }
  }
  function deletItem(index) {
    console.log(index);
    const newList = itemList.filter((item, i) => i !== index);
    setItemList(newList);
  }
  return (
    <div className="App">
      <h1>ToDo List</h1>
      <div className="App-header">
        <input
          onChange={(e) => setInText(e.target.value)}
          value={inText}
          placeholder="digite aqui"
        />
        <button onClick={addList} style={{ marginLeft: 20 }}>
          Adicionar
        </button>
        <ul>
          {itemList?.length ? (
            itemList.map((item, index) => (
              <li className="li">
                {(index, item)}
                <button onClick={deletItem(index, item)}>x</button>
              </li>
            ))
          ) : (
            <p>Lista v√°zia</p>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
